import React, { useState } from 'react';
import { useJournal } from '../hooks/useJournal';
import { useAuth } from '../hooks/useAuth';
import JournalEntryForm from '../components/journal/JournalEntryForm';
import EntryList from '../components/journal/EntryList';
import LoadingSpinner from '../components/common/LoadingSpinner';
import ErrorMessage from '../components/common/ErrorMessage';
import './Journal.css';

const Journal = () => {
    const { user } = useAuth();
    const { entries, loading, error, addEntry, updateEntry, deleteEntry, refetch } = useJournal();
    const [showNewEntry, setShowNewEntry] = useState(false);
    const [editingEntry, setEditingEntry] = useState(null);

    const handleSaveEntry = async (entryData) => {
        try {
            console.log('Saving entry:', entryData);
            if (editingEntry) {
                await updateEntry(editingEntry.id, entryData);
                setEditingEntry(null);
                console.log('Entry updated successfully');
            } else {
                await addEntry(entryData);
                setShowNewEntry(false);
                console.log('Entry added successfully');
            }
        } catch (error) {
            console.error('Error saving entry:', error);
            alert('Failed to save entry. Please try again.');
        }
    };

    const handleEditEntry = (entry) => {
        setEditingEntry(entry);
        setShowNewEntry(false);
    };

    const handleDeleteEntry = async (entryId) => {
        if (window.confirm('Are you sure you want to delete this entry?')) {
            try {
                await deleteEntry(entryId);
                console.log('Entry deleted successfully');
            } catch (error) {
                console.error('Error deleting entry:', error);
                alert('Failed to delete entry. Please try again.');
            }
        }
    };

    const handleCancelEdit = () => {
        setEditingEntry(null);
        setShowNewEntry(false);
    };

    if (loading) {
        return (
            <div className="journal-page">
                <LoadingSpinner />
            </div>
        );
    }

    return (
        <div className="journal-page">
            <div className="journal-header">
                <h1>Journal</h1>
                <p>Capture your thoughts and track your mood</p>
                {!showNewEntry && !editingEntry && (
                    <button 
                        className="new-entry-btn"
                        onClick={() => setShowNewEntry(true)}
                    >
                        ✏️ New Entry
                    </button>
                )}
            </div>

            <div className="journal-content">
                {/* Error Display */}
                {error && (
                    <ErrorMessage
                        error={error}
                        onRetry={refetch}
                        type="error"
                    />
                )}

                {/* New Entry Form */}
                {showNewEntry && (
                    <div className="entry-form-container">
                        <JournalEntryForm
                            onSave={handleSaveEntry}
                            onCancel={handleCancelEdit}
                            isNew={true}
                        />
                    </div>
                )}

                {/* Edit Entry Form */}
                {editingEntry && (
                    <div className="entry-form-container">
                        <JournalEntryForm
                            entry={editingEntry}
                            onSave={handleSaveEntry}
                            onCancel={handleCancelEdit}
                            isEditing={true}
                        />
                    </div>
                )}

                {/* Entries List */}
                <div className="entries-section">
                    <h2>Your Entries</h2>
                    {entries && entries.length > 0 ? (
                        <EntryList
                            entries={entries}
                            onEdit={handleEditEntry}
                            onDelete={handleDeleteEntry}
                        />
                    ) : (
                        <div className="empty-state">
                            <div className="empty-icon">📝</div>
                            <h3>No entries yet</h3>
                            <p>Start your journaling journey by creating your first entry</p>
                            <button 
                                className="start-journaling-btn"
                                onClick={() => setShowNewEntry(true)}
                            >
                                Start Journaling
                            </button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Journal;
