import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import LoadingSpinner from '../common/LoadingSpinner';
import ErrorMessage from '../common/ErrorMessage';

const Login = () => {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [isLoading, setIsLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const { login, error, clearError } = useAuth();

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
        clearError();
        setSuccessMessage('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setSuccessMessage('');
        clearError();

        console.log('üöÄ Login attempt started for:', formData.email);

        try {
            await login(formData.email, formData.password);
            console.log('‚úÖ Login successful for:', formData.email);
            setSuccessMessage('Login successful! Redirecting...');
            
            // Clear form
            setFormData({
                email: '',
                password: ''
            });
        } catch (error) {
            console.error('‚ùå Login error:', {
                code: error.code,
                message: error.message,
                email: formData.email
            });
            
            // Error is already set in AuthContext, just log it here
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="auth-page">
            <div className="auth-container">
                <div className="auth-header">
                    <h1>Welcome to MoodMate</h1>
                    <p>Your AI-powered mood journal</p>
                </div>

                <form className="auth-form" onSubmit={handleSubmit}>
                    <h2>Sign In</h2>

                    {error && <ErrorMessage error={error} onDismiss={clearError} />}
                    
                    {successMessage && (
                        <div className="success-message" style={{
                            backgroundColor: '#d4edda',
                            color: '#155724',
                            padding: '10px',
                            borderRadius: '4px',
                            marginBottom: '15px',
                            border: '1px solid #c3e6cb'
                        }}>
                            {successMessage}
                        </div>
                    )}

                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                            placeholder="Enter your email"
                            disabled={isLoading}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                            placeholder="Enter your password"
                            disabled={isLoading}
                        />
                    </div>

                    <button
                        type="submit"
                        className="auth-button"
                        disabled={isLoading}
                    >
                        {isLoading ? <LoadingSpinner size="small" /> : 'Sign In'}
                    </button>

                    <div className="auth-footer">
                        <p>
                            Don't have an account? <Link to="/register">Sign up</Link>
                        </p>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Login;
