import { initializeApp } from 'firebase/app';
import { getAuth, connectAuthEmulator } from 'firebase/auth';

const firebaseConfig = {
    apiKey: process.env.REACT_APP_FIREBASE_API_KEY || "AIzaSyAUU6QKm7J9YN5DPQC0x8KVe1f7kLu0CpY", // Replace with your actual API key from Firebase Console
    authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN || "moodmate-backend-cc75b.firebaseapp.com",
    projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID || "moodmate-backend-cc75b",
    storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET || "moodmate-backend-cc75b.appspot.com",
    messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID || "942753116267", // Replace with your actual sender ID
    appId: process.env.REACT_APP_FIREBASE_APP_ID || "1:123456789012:web:abcdef1234567890" // Replace with your actual app ID
};

const app = initializeApp(firebaseConfig);
export const auth = getAuth(app);

// Connect to auth emulator only if explicitly enabled
if (process.env.REACT_APP_USE_FIREBASE_EMULATOR === 'true') {
    try {
        connectAuthEmulator(auth, 'http://localhost:9099');
    } catch (error) {
        // Emulator already connected or not available
    }
}

export default app;
